class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      URL: '',
    }
  }

  // handleInput = e => {
  //   let input = e.target.value;
  //   this.setState({ URL: input });
  //   console.log(input);
  // }

  handleClick = e => {
    e.preventDefault();
    let input = e.target.value;
    this.setState({ URL: input })
  }

  render() {
    return (
      <>
      <Header />
      <form onSubmit={this.handleClick}>
      <label for="URL" >URL:</label>
      <input name="URL" />
      <button type="submit" >Go!</button>

      </form>
      <p>{ this.state.URL}</p>
      <Footer />

      </>
    )
  }
}

export default App;
